insert into films (title, produced_date, country, slogan, budget, box_office_USA, box_office_world, realise_date_Russia, realise_date_World, duration, description, rating_film, poster) values
	('Зеленая миля', '1999', 'США', '«Пол Эджкомб не верил в чудеса. Пока не столкнулся с одним из них»', '600000000', '136801374', '286801674', '2000-04-18', '1999-12-09', '189', 'Пол Эджкомб - начальник блока смертников в тюрьме «Холодная гора», каждый из узников которого однажды проходит «зеленую милю» по пути к месту казни. Пол повидал много заключённых и надзирателей за время работы. Однако гигант Джон Коффи, обвинённый в страшном преступлении, стал одним из самых необычных обитателей блока.', '9.1', '12'),
 	('Побег из Шоушенка','1994','США','«Страх - это кандалы. Надежда - это свобода»',25000000,28341469,28418687,'2019-10-24','1994-09-10',142,'Бухгалтер Энди Дюфрейн обвинён в убийстве собственной жены и её любовника. Оказавшись в тюрьме под названием Шоушенк, он сталкивается с жестокостью и беззаконием, царящими по обе стороны решётки. Каждый, кто попадает в эти стены, становится их рабом до конца жизни. Но Энди, обладающий живым умом и доброй душой, находит подход как к заключённым, так и к охранникам, добиваясь их особого к себе расположения.',9.1,22),
	('Властелин колец: Возвращение Короля','2003','Новая Зеландия','«There can be no triumph without loss. No victory without suffering. No freedom without sacrifice»',94000000,377027325,1118881224,'2004-01-24','2003-12-01',201,'Последняя часть трилогии о Кольце Всевластия и о героях, взявших на себя бремя спасения Средиземья. Повелитель сил Тьмы Саурон направляет свои бесчисленные рати под стены Минас-Тирита, крепости Последней Надежды. Он предвкушает близкую победу, но именно это и мешает ему заметить две крохотные фигурки - хоббитов, приближающихся к Роковой Горе, где им предстоит уничтожить Кольцо Всевластия. Улыбнется ли им счастье?',8.6,17),
	('Интерстеллар','2014','США','"Следующий шаг человечества станет величайшим"',165000000,188020017,677463813,'2014-11-06','2014-10-26',169,'Когда засуха, пыльные бури и вымирание растений приводят человечество к продовольственному кризису, коллектив исследователей и учёных отправляется сквозь червоточину (которая предположительно соединяет области пространства-времени через большое расстояние) в путешествие, чтобы превзойти прежние ограничения для космических путешествий человека и найти планету с подходящими для человечества условиями.',8.6,14),
	('Властелин колец: Братство кольца','2004','США','«Power can be held in the smallest of things...»',93000000,313364114,868385360,'2002-02-04','2001-12-10',178,'Сказания о Средиземье - это хроника Великой войны за Кольцо, войны, длившейся не одну тысячу лет. Тот, кто владел Кольцом, получал власть над всеми живыми тварями, но был обязан служить злу.',8.6,13),
	('Список Шиндлера','1993','США','«Этот список - жизнь»',22000000,96065768,321306305,'1994-05-21','1993-11-30',195,'Фильм рассказывает реальную историю загадочного Оскара Шиндлера, члена нацистской партии, преуспевающего фабриканта, спасшего во время Второй мировой войны почти 1200 евреев.',8.8,22),
	('Форрест Гамп','1994','США','«Мир уже никогда не будет прежним, после того как вы увидите его глазами Форреста Гампа»',55000000,329694254,677854752,'2020-02-13','1994-06-23',142,'От лица главного героя Форреста Гампа, слабоумного безобидного человека с благородным и открытым сердцем, рассказывается история его необыкновенной жизни.',8.9,27),
	('Клаус','2019','Испания','«Discover the friendship that launched a legend»',40000000,null,null,'2019-11-09',null,96,'Владелец почтовой империи, чтобы научить ленивого отпрыска по имени Джеспер уму-разуму, отправляет его на крайний север в город Смиренсбург. Тот должен организовать там почтовое отделение и за год обработать не менее 6000 писем. Прибыв на место, парень оказывается в зоне боевых действий: два семейных клана, так уж исторически сложилось, питают взаимную ненависть и свято чтут многовековую традицию при любой возможности мутузить друг друга и делать пакости. Разумеется, в такой обстановке не до писем, и почтовое отделение давно превратилось в курятник. Уже практически отчаявшись, Джеспер посещает лесного отшельника по имени Клаус, и их знакомство положит начало удивительным событиям.',8.6,32),
	('В бой идут одни «старики»','1973','СССР',null,500000,null,null,'1974-08-12',null,87,'Эта эскадрилья стала «поющей» - так капитан Титаренко подбирал себе новичков. Его «старикам» было не больше двадцати, но «желторотиков», пополнение из летных училищ ускоренного выпуска, в бой все равно, по возможности, не пускали.',8.7,35),
	('Назад в будущее','1985','США','«Семнадцатилетний Марти МакФлай пришел вчера домой пораньше. На 30 лет раньше»',19000000,210609754,381109567,'2019-06-13','1985-07-03',116,'Подросток Марти с помощью машины времени, сооружённой его другом-профессором доком Брауном, попадает из 80-х в далекие 50-е. Там он встречается со своими будущими родителями, ещё подростками, и другом-профессором, совсем молодым.',8.6,36),
	('Иван Васильевич меняет профессию','1973','СССР',null,null,null,null,null,'1973-09-17',88,'Инженер-изобретатель Тимофеев сконструировал машину времени, которая соединила его квартиру с далеким шестнадцатым веком - точнее, с палатами государя Ивана Грозного. Туда-то и попадают тезка царя пенсионер-общественник Иван Васильевич Бунша и квартирный вор Жорж Милославский.',8.8,37),
	('Король Лев','1994','США','«The Circle of Life»',45000000,312855434,764455316,'2012-03-26','1994-06-12',88,'У величественного Короля-Льва Муфасы рождается наследник по имени Симба. Уже в детстве любознательный малыш становится жертвой интриг своего завистливого дяди Шрама, мечтающего о власти.',8.8,49),
	('Тайна Коко','2017','США','«The celebration of a lifetime»',175000000,209726645,807086123,'2017-11-27','2017-10-20',105,'12-летний Мигель живёт в мексиканской деревушке в семье сапожников и тайно мечтает стать музыкантом. Тайно — потому что в его семейном клане музыка считается проклятием. Когда-то его прапрадед оставил свою жену, прапрабабку Мигеля, ради мечты, которая теперь не даёт спокойно жить Мигелю. С тех пор музыкальная тема в семье стала табу. Мигель обнаруживает, что между ним и его любимым певцом Эрнесто де ла Крусом, ныне покойным, существует некая — пока неназванная — связь. Паренёк отправляется к своему кумиру в Страну Мёртвых, где встречает души своих предков. Мигель знакомится там с духом-трикстером по имени Гектор (в облике скелета), который становится его проводником. Вдвоём они отправляются на поиски де ла Круса.',8.6,67),
	('Большой куш','2000','Великобритания','«Stealing stones is hazardous»',10000000,30328457,83557136,'2001-05-21','2000-08-13',104,'Четырехпалый Френки должен был переправить краденый алмаз из Англии в США своему боссу Эви. Но вместо этого герой попадает в эпицентр больших неприятностей. Сделав ставку на подпольном боксерском поединке, Френки попадает в круговорот весьма нежелательных событий.',8.5,72),
	('Карты, деньги, два ствола','1998','Великобритания','«They lost half a million at cards but they''ve still got a few tricks up their sleeve»',960000,3753648,3753648,'1999-05-06','1998-08-23',107,'Четверо молодых парней накопили каждый по 25 тысяч фунтов, чтобы один из них мог сыграть в карты с опытным шулером и матерым преступником, известным по кличке Гарри-Топор. Парень в итоге проиграл 500 тысяч, на уплату долга ему дали неделю.',8.5,78),
	('Криминальное чтиво','1994','США','«Just because you are a character doesn''t mean you have character»',8000000,107928624,213928453,'1995-09-25','1994-05-21',157,'Двое бандитов Винсент Вега и Джулс Винфилд ведут философские беседы в перерывах между разборками и решением проблем с должниками криминального босса Марселласа Уоллеса.',8.6,75),
	('Начало','2010','США','«Твой разум - место преступления»',160000000,292576328,828364215,'2010-07-27','2010-07-10',148,'Кобб – талантливый вор, лучший из лучших в опасном искусстве извлечения: он крадет ценные секреты из глубин подсознания во время сна, когда человеческий разум наиболее уязвим. Редкие способности Кобба сделали его ценным игроком в привычном к предательству мире промышленного шпионажа, но они же превратили его в извечного беглеца и лишили всего, что он когда-либо любил.',8.6,74),
	('Унесённые призраками','2001','Япония','«The tunnel led Chihiro to a mysterious town...»',19000000,13750216,355217963,'2002-12-31','2001-07-20',125,'Тихиро с мамой и папой переезжают в новый дом. Заблудившись по дороге, они оказываются в странном пустынном городе, где их ждет великолепный пир. Родители с жадностью набрасываются на еду и к ужасу девочки превращаются в свиней, став пленниками злой колдуньи Юбабы. Теперь, оказавшись одна среди волшебных существ и загадочных видений, Тихиро должна придумать, как избавить своих родителей от чар коварной старухи.',8.4,81),
	('ВАЛЛ·И','2008','США','«Любовь - дело техники»',180000000,223808354,521311649,'2008-07-08','2008-06-27',98,'Робот ВАЛЛ·И из года в год прилежно трудится на опустевшей Земле, очищая нашу планету от гор мусора, которые оставили после себя улетевшие в космос люди. Он и не представляет, что совсем скоро произойдут невероятные события, благодаря которым он встретит друзей, поднимется к звездам и даже сумеет изменить к лучшему своих бывших хозяев, совсем позабывших родную Землю.',8.3,82),
	('...А зори здесь тихие','1972','СССР',null, null, null, null,'1972-08-22',null, 160,'В прифронтовой полосе группа девушек-зенитчиц вынуждена вступить в неравный бой с вражескими десантниками. Эти девчонки мечтали о большой любви, нежности, семейном тепле - но на их долю выпала жестокая война, и они до конца выполнили свой воинский долг…',8.5,84),
	('Довод','2020','Великобритания','«Время уходит»',205000000,54400000,357800000,'2020-09-03','2020-08-22',150,'После теракта в киевском оперном театре агент ЦРУ объединяется с британской разведкой, чтобы противостоять русскому олигарху, который сколотил состояние на торговле оружием. Для этого агенты используют инверсию времени - технологию будущего, позволяющую времени идти вспять.',7.8,85),
	('Зов предков','2020','Канада','«Based on the Legendary Novel»',null, 62342658,107604248,'2020-02-20','2020-02-19',100,'История Бака, дружелюбного пса, чья размеренная домашняя жизнь перевернулась с ног на голову во времена золотой лихорадки в 1880-х, когда его вырвали из дома в Калифорнии и перевезли в дикую и холодную Аляску. Будучи новичком в упряжке почтовой службы, а впоследствии лидером, Бак попадает в невероятное приключение, находит свое место в мире и становится хозяином своей жизни.',7.2,88),
	('Тор: Рагнарёк','2017','США','«No Hammer. No Problem»',180000000,315058456,853977123,'2017-11-02','2017-10-10',130,'Вернувшись в Асгард в поисках таинственного врага, ведущего охоту на Камни Бесконечности, Тор обнаруживает, что действия его брата Локи, захватившего трон Асгарда, привели к приближению наиболее страшного события - Рагнарёка. По легенде это ознаменует последнюю битву Асгарда, последствием которой станет его полное уничтожение. В попытке предотвратить это событие Тору предстоит прибегнуть к помощи своего товарища из Мстителей - Халка. Вместе им предстоит столкнуться лицом к лицу со злейшим врагом всех девяти миров - огненным демоном Суртуром, целью которого является исполнение пророчества о Рагнарёке и уничтожение девяти миров.',7.6,89),
	('Стражи Галактики. Часть 2','2017','США','«This summer, the galaxy won''t save itself»',200000000,389819356,863756459,'2017-05-04','2017-04-10',136,'Все в сборе: землянин Питер Квилл (Звездный Лорд), молчаливый громила Дракс, зеленокожая наемница Гамора, живое дерево Грут и говорящий енот. Герои не изменяют себе и с завидной регулярностью продолжают попадать в немыслимые ситуации, выпутываясь из них почти без ущерба (а иногда даже с пользой) для окружающих. На этот раз им предстоит раскрыть одну из самых главных тайн во всей Галактике: кто же на самом деле отец Питера Квилла?',7.5,91),
	('Мстители: Война бесконечности','2018','США','«An entire universe. Once and for all»',321000000,618615235,2048567985,'2018-05-03','2018-04-28',149,'Пока Мстители и их союзники продолжают защищать мир от различных опасностей, с которыми не смог бы справиться один супергерой, новая угроза возникает из космоса: Танос. Межгалактический тиран преследует цель собрать все шесть Камней Бесконечности - артефакты невероятной силы, с помощью которых можно менять реальность по своему желанию. Всё, с чем Мстители сталкивались ранее, вело к этому моменту – судьба Земли никогда ещё не была столь неопределённой.',7.9,94),
	('Престиж','2006','Великобритания','«Дружба, ставшая соперничеством. Соперничество, превратившееся во вражду»',40000000,53089342,109676421,'2007-01-18','2006-10-16',125,'Роберт и Альфред - фокусники-иллюзионисты, которые на рубеже XIX и XX веков соперничали друг с другом в Лондоне. С годами их дружеская конкуренция на профессиональной почве перерастает в настоящую войну.',8.5,92),
	('Они никогда не станут старше','2018','Великобритания','«A ground-breaking documentary...»',null, 17549352,19866234,'2018-11-17','2018-11-16',99,'Они были солдатами. И молодыми. Им не повезло: их лучшие годы сожрала Первая мировая – первая глобальная катастрофа ХХ века, у западных историков получившая название Великая война. Но их лица и улыбки остались в кадрах хроники.',8.2,98),
	('МГЛА','2007','США','«Fear Changes Everything»',18000000,25594236,57297354,'2007-11-22','2007-11-07',126,'Маленький городок накрывает сверхъестественный туман, отрезая людей от внешнего мира. Группе героев, оказавшихся в этот момент в супермаркете, приходится вступить в неравный бой с обитающими в тумане монстрами.',7.6,99),
	('Поймай меня, если сможешь','2002','США','«Правдивая история о реальном преступнике»',52000000,164615234,352114653,'2003-02-13','2002-12-12',141,'Фрэнк Эбегнейл успел поработать врачом, адвокатом и пилотом на пассажирской авиалинии – и все это до достижения полного совершеннолетия в 21 год. Мастер в обмане и жульничестве, он также обладал искусством подделки документов, что в конечном счете принесло ему миллионы долларов, которые он получил по фальшивым чекам.',8.5,102),
	('Балто','1995','США','«Part Dog. Part Wolf. All Hero»',null, 11348125,11348125,null,'1995-12-25',71,'Наполовину лайка, наполовину волк, Балто и сам не знает кто он такой на самом деле. Всеми гонимый бродяга в ледяной пустыне Аляски. Только его друзья русский полярный гусь Борис, медвежата Мак и Лак и красавица-лайка Дженна уверены, что хотя он и не такой как все, но в его груди бьется благородное сердце.',8.3,106),
	('Изгой','2000','США','«At the edge of the world, his journey begins»',90000000,233632124,429632587,'2001-01-21','2000-12-07',143,'Герой этого фильма, Чак Ноланд, - сотрудник всемирно известной службы доставки «Федерал Экспресс». Чак - скрупулезный практик и неисправимый педант. Жизнь Ноланда, высокопоставленного инспектора международных отделений «ФедЕкс» расписана по минутам. И этих драгоценных минут катастрофически не хватает ни на личную жизнь, ни на любимую женщину...',8.3,109),
	('Аты-баты, шли солдаты...','1976','СССР',null, null, null, null, '1977-04-15',null, 87,'Они погибли в 1944 году, их было всего восемнадцать человек - комсомольский взвод, остановивший колонну немецких танков.',8.1,119),
	('Операция «Ы» и другие приключения Шури','1965','СССР',null, null, null, null, '1965-06-23',null, 95,'Фильм состоит из трех новелл, объединенных фигурой главного героя Шурика, попадающего в самые невероятные ситуации. В «Напарнике» Шурик сражается с хулиганом Верзилой; в «Наваждении» весьма оригинальным способом готовится к экзамену; в «Операции «Ы» предотвращает «ограбление века», на которое идет знаменитая тройка - Балбес, Трус и Бывалый.',8.7,125),
	('Алландин','1992','США','«Imagine if you had three wishes, three hopes, three dreams and they all could come true»',28000000,217350265,504050364,null,'1992-11-08',90,'В славном граде Аграба живёт Аладдин, веселый воришка с золотым сердцем. Живёт и не знает, что вскоре его ждёт величайшее приключение в жизни. Ведь только с его помощью чёрный маг Джафар может овладеть невероятным сокровищем — лампой, в которой заключён исполняющий желания могущественный Джинн. И только хитроумие, смекалка и доброта Аладдина и его друзей — обезьянки Абу, принцессы Жасмин и самого Джинна - способны помешать Джафару завладеть лампой и захватить власть над миром.',8.1,124),
	('Корпорация монстров','2001','США','«We Think They Are Scary, But Really We Scare Them!»',115000000,255873459,528773658,'2002-03-21','2001-10-28',92,'Склизкий гад в сливном бачке, мохнатый зверь, похожий на чудовище из «Аленького цветочка», гигантские мокрицы под кроватью — все они существуют на самом деле. Все, что им нужно — пугать детей, потому что из детских криков они получают электричество.',8.1,129),
	('История игрушек','1995','США','«Proud to be a vegetable»',30000000,191796258,373554159,'2009-10-01','1995-11-18',81,'Каждый ребенок верит, что когда он оставляет свои игрушки одни, они начинают заниматься своими делами. Этот мультипликационный фильм позволяет убедиться им в своей правоте.',7.9,134),
	('В поисках Немо','2003','США','«From the Creators of Monsters, Inc»',94000000,339714282,895614458,'2003-12-25','2003-05-30',101,'Среди прекрасных тропических морских стихий, в районе Большого барьерного рифа в уединении живет рыба-клоун по имени Марлин. Он растит своего единственного сыночка Немо. Океан и существующие в нем опасности очень страшат Марлина, и он как может ограждает сына от них, но молодой Немо, страдающий излишним любопытством, очень хочет разузнать побольше о таинственном рифе, рядом с которым они живут.',7.9,147),
	('Джанго освобожденный','2012','США','«Они забрали его свободу. Он заберёт у них всё»',100000000,162805957,425638456,'2013-01-17','2012-12-11',165,'Эксцентричный охотник за головами, также известный как Дантист, промышляет отстрелом самых опасных преступников. Работенка пыльная, и без надежного помощника ему не обойтись. Но как найти такого и желательно не очень дорогого? Освобождённый им раб по имени Джанго – прекрасная кандидатура. Правда, у нового помощника свои мотивы – кое с чем надо сперва разобраться.',8.2,146),
	('Ходячий замок','2004','Япония',null, 24000000,5576568,232213485,'2005-08-25','2004-09-05',119,'Злая ведьма заточила 18-летнюю Софи в тело старухи. Девушка-бабушка бежит из города куда глаза глядят и встречает удивительный дом на ножках, где знакомится с могущественным волшебником Хаулом и демоном Кальцифером. Кальцифер должен служить Хаулу по договору, условия которого он не может разглашать. Девушка и демон решают помочь друг другу избавиться от злых чар.',8.3,145),
	('Мой сосед Тоторо','1988','Япония',null, 3700000,2250313,30541597,'2008-03-20','1988-04-18',86,'Сестры Сацуки и Мэй переезжают вместе с папой в деревенский дом. Однажды девочки обнаруживают, что по соседству с ними живут лесные духи — хранители леса во главе со своим могущественным, но добрым повелителем Тоторо. Постепенно Тоторо становится другом девочек, помогая им в их повседневных приключениях.',8.2,151),
	('Леон','1994','Франция','«Вы не можете остановить того, кого не видно»',20000000,19501597,19552354,null, '1994-09-14',133,'Профессиональный убийца Леон неожиданно для себя самого решает помочь 11-летней соседке Матильде, семью которой убили коррумпированные полицейские.',8.7,154),
	('Пятый элемент','1997','Франция','«It Mu5t Be Found»',90000000,63280157,263920487,'1997-09-27','1997-05-07',126,'Каждые пять тысяч лет открываются двери между измерениями и темные силы стремятся нарушить существующую гармонию. Каждые пять тысяч лет Вселенной нужен герой, способный противостоять этому злу. XXIII век. Нью-йоркский таксист Корбен Даллас должен решить глобальную задачу - спасение всего рода человеческого.',8.1,158),
	('Такси','1998','Франция',null, 25000000,268254,268254,'1998-10-28','1998-04-08',86,'Молодой таксист Даниэль помешан на быстрой езде. Как ураган, проносится он по извилистым улицам Марселя на своём мощном ревущем звере «Пежо», пугая пассажиров и прохожих. Неподкупный полицейский Эмильен вынуждает его помочь в поимке банды грабителей, ускользающих от полиции на своих неуловимых «Мерседесах». И до самого конца не ясно, кто же сможет удержаться на крутом вираже.',7.9,155),
	('Зеленая книга','2018','США','«Inspired by a True Friendship»',23000000,85080457,321725842,'2019-01-24','2018-09-11',130,'1960-е годы. После закрытия нью-йоркского ночного клуба на ремонт вышибала Тони по прозвищу Болтун ищет подработку на пару месяцев. Как раз в это время Дон Ширли, утонченный светский лев, богатый и талантливый чернокожий музыкант, исполняющий классическую музыку, собирается в турне по южным штатам, где ещё сильны расистские убеждения и царит сегрегация. Он нанимает Тони в качестве водителя, телохранителя и человека, способного решать текущие проблемы. У этих двоих так мало общего, и эта поездка навсегда изменит жизнь обоих.',8.3,164),
	('Я, снова я и Ирэн','2000','США','«From gentle to mental»',51000000,90570165,149270598,'2000-08-03','2000-06-15',116,'Чарли - добрый и отзывчивый патрульный полицейский. Но вот беда – он страдает раздвоением личности. Когда кто-то пытается надавить на него, появляется Хэнк – второе «Я» Чарли. Это грубый, жестокий, агрессивный и невоспитанный тип, любитель выпивки и сквернословия.',7.2,167),
	('Москва слезам не верит','1979','СССР','«The Funniest, Tenderest Love Story of the Year»',900000,2500000,2500000,'1980-01-11',null,150,'Москва пятидесятых годов. Три молодые провинциалки приезжают в Москву в поисках того, что ищут люди во всех столицах мира - любви, счастья и достатка. Их судьбы складываются именно так, как предполагает характер каждой из девушек.',8.4,168),
	('Любовь и голуби','1984','СССР',null, null, null, null, '1985-01-07',null, 107,'Ликвидируя неисправность лебедки, Василий Кузякин получил травму и путевку на юг. Встретил роковую женщину Раису Захаровну и... вернулся Вася с курорта не к себе в деревню, а в дом Раисы Захаровны. Началась для него новая жизнь, в которой было много непонятного и интересного, но не было дома, где остались Надя, дети и голуби...',8.3,180),
	('Спасти рядового Райана','1998','США','«The movie that inspired to the world to remember»',70000000,216540597,491840597,'1998-10-09','1998-07-21',169,'Капитан Джон Миллер получает тяжелое задание. Вместе с отрядом из восьми человек Миллер должен отправиться в тыл врага на поиски рядового Джеймса Райана, три родных брата которого почти одновременно погибли на полях сражений.',8.2,189),
	('Джентльмены','2019','США','"Criminal. Class"',22000000,36471654,115171354,'2020-02-13','2019-12-03',113,'Один ушлый американец ещё со студенческих лет приторговывал наркотиками, а теперь придумал схему нелегального обогащения с использованием поместий обедневшей английской аристократии и очень неплохо на этом разбогател. Другой пронырливый журналист приходит к Рэю, правой руке американца, и предлагает тому купить киносценарий, в котором подробно описаны преступления его босса при участии других представителей лондонского криминального мира - партнёра-еврея, китайской диаспоры, чернокожих спортсменов и даже русского олигарха.',8.25,190),
	('Далласский клуб покупателей','2013','США','«Dare to Live»',5000000,27298285,55738458,'2017-02-27','2013-09-07',118,'Реальная история Рона Вудруфа, техасского электрика, у которого в 1985 году обнаружили СПИД. Врачи отвели ему всего 30 дней, но он не пожелал смириться со смертным приговором и сумел продлить свою жизнь, принимая нетрадиционные лекарства, а затем наладил подпольный бизнес по продаже их другим больным.',7.8,191);
